(()=>{const e=document.querySelector(".button__burger"),n=document.querySelector(".side-bar");e.addEventListener("click",(function(){n.style.display="flex"})),document.getElementById("closeSideBar").addEventListener("click",(function(){n.style.display="none"})),document.addEventListener("click",(function(t){!n.contains(t.target)&&!e.contains(t.target)&&window.innerWidth<1366&&(n.style.display="none")})),window.addEventListener("resize",(function(){document.documentElement.clientWidth>=1366?n.style.display="flex":n.style.display="none"}))})();